/**
 * @name autobind
 * @type {function}
 * @param target
 * @param propertyKey
 * @param descriptor
 * @returns {any}
 * @description Method decorator that binds it to the instance of the class it is defined on
 */
export declare function alias(...aliases: string[]): (target: any, propertyKey: string, descriptor: PropertyDescriptor) => {
    configurable: boolean;
    enumerable: boolean;
    get(): any;
};
